pragma solidity ^0.5.0;

contract DeCloudFiles { 

    struct FileDetails {
        string hashValueOfFile; 
        string nameOfFile;  
        string typeOfFile;
        string storedIn;
        uint256 splitInto;
    }

    mapping (address => FileDetails[]) files;
    mapping (address => uint256) noOfFiles;
    mapping (address => uint) amountGainedBy; 

    function addFile (string memory filehash, string memory fileName, string memory fileType,string memory storedIn,address storageOwner,uint256 splitInto) public payable {
        files[msg.sender].push(FileDetails(filehash,fileName,fileType,storedIn,splitInto));
        noOfFiles[msg.sender]++;
        amountGainedBy[storageOwner] += msg.value;
    }

    function getFile(uint256 position) public view returns (string memory, string memory, string memory, string memory,uint256) {
        FileDetails memory file = files[msg.sender][position];
        return ( file.hashValueOfFile, file.nameOfFile, file.typeOfFile, file.storedIn,file.splitInto);
    }

    function getNoOfFile() public view returns (uint256){
        return noOfFiles[msg.sender];
    }

    function getAmount() public view returns (uint){
        return amountGainedBy[msg.sender];
    }

    function getPaid() public payable{
        uint amount = amountGainedBy[msg.sender];
        require(amount > 0,"Insufficient money");
        require(msg.sender.send(amount) == true,"Transaction Failed");
        amountGainedBy[msg.sender] = 0;
    }
}
