pragma solidity ^0.5.0;

contract DeCloudFiles { 

    struct FileDetails {
        string hashValueOfFile; 
        string nameOfFile;  
        string typeOfFile;
        string storedIn;
    }

    mapping (address => FileDetails[]) files;
    mapping (address => uint256) noOfFiles;

    function addFile (string memory filehash, string memory fileName, string memory fileType,string memory storedIn) public {
        files[msg.sender].push(FileDetails(filehash,fileName,fileType,storedIn));
        noOfFiles[msg.sender]++;
    }

    function getFile(uint256 position) public view returns (string memory, string memory, string memory, string memory) {
        FileDetails memory file = files[msg.sender][position];
        return ( file.hashValueOfFile, file.nameOfFile, file.typeOfFile, file.storedIn );
    }

    function getNoOfFile() public view returns (uint256){
        return noOfFiles[msg.sender];
    }

}
